mapper.select:
select rn, CORD_NUM, cGoodsNum, CORD_QTY, CORD_REGIST, goods_name from (select rownum rn, CORD_NUM, cGoodsNum, CORD_QTY, CORD_REGIST, goods_name from ( select rownum rn, CORD_NUM, c.GOODS_NUM cGoodsNum, CORD_QTY, CORD_REGIST, goods_name from CORD c join goods g on c.goods_num = g.goods_num
and cord_num = #{num}
order by cord_NUM desc)) where rn >= #{startPage} and rn <= #{endPage}
